      this.handleClick = this.handleClick.bind(this);
    }
  
    handleClick() {
      this.setState({
        clicked: true
      });
    }
  
    render() {
      return (
        <div>
          <Button onClick={this.handleClick} />
          {this.state.clicked ? <NewComponent /> : null}
        </div>
      );
    }
  };

export default ImageCard;
