import React, {useState} from 'react';

const SearchBar = ({ onsubmit }) => {

    const [text, setText] = useState('');
    const onFormSubmit = (e) => {
        e.preventDefault();
        onsubmit(text);
    }

    return (
        <React.Fragment>
            <form className='form-group row mt-3' onSubmit={onFormSubmit}>
            <label htmlFor='search' className='col-sm-1 col-form-label' style={{
                fontWeight:'bold'
            }}>
                Search 
            </label>
            <div className='col-sm-11'>
                <input type='text' className='form-control' id='search' value={text} onChange={(e)=>setText(e.target.value)} placeholder='Search . . .'/>
            </div>
           
        </form>
         <hr style={{
            backgroundColor:'whitesmoke'
        }}
        />
        </React.Fragment>
        
    );
}

export default SearchBar;
